	/**
	 * Checks if the email field of a client respects an email specific pattern and throws an exception if the condition is not met.
	 *
	 * @param client client to vaidate
	 */
	public void validate(Client client) {
		Pattern pattern = Pattern.compile(EMAIL_PATTERN);
		if (!pattern.matcher(client.getEmail()).matches()) {
			JOptionPane.showMessageDialog(null, "Insert a valid email address",
					"ERROR: invalid email", JOptionPane.INFORMATION_MESSAGE);
			throw new IllegalArgumentException("Email is not a valid email!");
		}
	}

